// src/components/HomePage.js - 完整结构
import React, { useEffect, useState } from 'react';
import { BarChart3, FolderOpen, Workflow, ArrowRight, Award, Clock, Shield, Brain, Users, Target, Zap } from 'lucide-react';

const HomePage = ({ setCurrentPage }) => {
  const [activeSection, setActiveSection] = useState('home');

  // 监听滚动以更新导航状态
  useEffect(() => {
    const handleScroll = () => {
      const sections = ['hero', 'about', 'services', 'features'];
      const scrollPosition = window.scrollY + 100;

      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const { offsetTop, offsetHeight } = element;
          if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
            const pageMap = {
              'hero': 'home',
              'about': 'about',
              'services': 'services',
              'features': 'home'
            };
            setActiveSection(pageMap[section] || 'home');
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const stats = [
    { number: '15+', label: 'Years Experience' },
    { number: '500+', label: 'Clients Worldwide' },
    { number: '98%', label: 'Success Rate' }
  ];

  const services = [
    {
      id: 'single',
      icon: BarChart3,
      title: 'Single Analysis',
      description: 'Extract cybersecurity skill keywords from a single job description across 6 specialized domains.',
      features: ['Real-time analysis', 'AI-powered extraction', 'Detailed reports']
    },
    {
      id: 'batch',
      icon: FolderOpen,
      title: 'Batch Processing',
      description: 'Upload multiple cybersecurity job ads and extract structured skill data efficiently.',
      features: ['Bulk file processing', 'CSV export', 'Progress tracking']
    },
    {
      id: 'course',
      icon: Workflow,
      title: 'Course Mapping',
      description: 'Align course content with real-world cybersecurity job market requirements.',
      features: ['Curriculum alignment', 'Gap analysis', 'Learning pathways']
    }
  ];

  const features = [
    {
      icon: Award,
      title: 'Industry Alignment',
      description: 'Continuously updated with the latest skill sets and technology demands from the cybersecurity job market, ensuring alignment with real-world industry needs.'
    },
    {
      icon: Brain,
      title: 'AI-Enhanced Skill Analysis',
      description: 'Powered by large language models and Sparse Encoder techniques to extract and interpret skills accurately while capturing contextual variations across job descriptions.'
    },
    {
      icon: Clock,
      title: 'Timely Delivery',
      description: 'Fast processing times with real-time analysis and instant results delivery.'
    },
    {
      icon: Shield,
      title: 'Privacy by Design',
      description: 'No user data is stored or transmitted to external servers. All analyses are performed locally with LLMs deployed on edge servers to ensure privacy, control, and compliance.'
    }
  ];

  const aboutFeatures = [
    {
      icon: Users,
      title: '500+ Global Clients',
      description: 'Trusted by organizations worldwide for accurate skill analysis and job market insights.'
    },
    {
      icon: Target,
      title: '6 Cybersecurity Domains',
      description: 'Specialized expertise across Security Architecture, Analysis, Consulting, Operations, Information Security, and Testing.'
    },
    {
      icon: Zap,
      title: 'AI-Powered Technology',
      description: 'Cutting-edge algorithms that deliver 98% accuracy in skill extraction and matching.'
    }
  ];

  return (
    <div className="pt-16">
      {/* Hero Section */}
      <section id="hero" className="pt-24 pb-20 bg-gradient-to-br from-green-50 via-emerald-50 to-teal-50 relative overflow-hidden">
        {/* Background Elements */}
        <div className="absolute top-20 right-20 w-72 h-72 bg-gradient-to-r from-green-400 to-emerald-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute bottom-20 left-20 w-96 h-96 bg-gradient-to-r from-emerald-400 to-teal-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-1000"></div>

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
          <div className="grid lg:grid-cols-2 gap-12 items-center">
            <div className="space-y-8">
              <div className="space-y-4">
                <div className="inline-flex items-center px-4 py-2 bg-green-100 text-green-800 rounded-full text-sm font-medium">
                  <span className="w-2 h-2 bg-green-500 rounded-full mr-2 animate-pulse"></span>
                  AI-Powered Analysis Platform
                </div>
                <h1 className="text-5xl lg:text-6xl font-bold text-gray-900 leading-tight">
                  Strategic Solutions for{' '}
                  <span className="bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent">
                    Skill Analysis And Curriculum Mapping
                  </span>
                </h1>
                <p className="text-xl text-gray-600 leading-relaxed">
                  Transform job descriptions into actionable skill insights with our AI-powered analysis platform, which helps students explore career-aligned skills
                  and enables educators to map course content across six cybersecurity domains.
                </p>
              </div>

              <div className="flex flex-col sm:flex-row gap-4">
                <button
                  onClick={() => setCurrentPage('services')}
                  className="group bg-gradient-to-r from-green-600 to-emerald-600 text-white px-8 py-4 rounded-xl font-semibold hover:from-green-700 hover:to-emerald-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:scale-105 flex items-center justify-center gap-2"
                >
                  Start Analysis
                  <ArrowRight className="h-5 w-5 group-hover:translate-x-1 transition-transform" />
                </button>
                <button
                  onClick={() => document.getElementById('about').scrollIntoView({ behavior: 'smooth' })}
                  className="px-8 py-4 border-2 border-green-600 text-green-600 rounded-xl font-semibold hover:bg-green-600 hover:text-white transition-all duration-200 flex items-center justify-center gap-2"
                >
                  Learn More
                </button>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-3 gap-8 pt-8">
                {stats.map((stat, index) => (
                  <div key={index} className="text-center">
                    <div className="text-3xl font-bold text-gray-900 mb-1">{stat.number}</div>
                    <div className="text-gray-600 text-sm font-medium">{stat.label}</div>
                  </div>
                ))}
              </div>
            </div>

            {/* Hero Visual */}
            <div className="relative">
              <div className="bg-white/60 backdrop-blur-sm rounded-3xl p-8 shadow-2xl border border-white/20">
                <div className="space-y-6">
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg flex items-center justify-center">
                      <BarChart3 className="h-6 w-6 text-white" />
                    </div>
                    <div className="flex-1">
                      <div className="h-3 bg-gray-200 rounded-full mb-2">
                        <div className="h-3 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full w-4/5 animate-pulse"></div>
                      </div>
                      <div className="h-2 bg-gray-200 rounded-full w-3/5"></div>
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-3">
                    {['Security Analysis', 'Cloud Security', 'Network Security'].map((skill, index) => (
                      <div key={index} className="bg-gradient-to-r from-green-100 to-emerald-100 text-green-800 text-xs px-3 py-2 rounded-lg text-center font-medium">
                        {skill}
                      </div>
                    ))}
                  </div>

                  <div className="space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="text-sm font-medium text-gray-700">Match Score</span>
                      <span className="text-sm font-bold text-green-600">95%</span>
                    </div>
                    <div className="h-2 bg-gray-200 rounded-full">
                      <div className="h-2 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full w-[95%] transition-all duration-1000"></div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* About Section */}
      <section id="about" className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">About SkillExtractor</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Leading the future of AI-powered skill analysis and job market intelligence with 15+ years of expertise
            </p>
          </div>

          <div className="grid lg:grid-cols-2 gap-16 items-center mb-16">
            <div>
              <h3 className="text-3xl font-bold text-gray-900 mb-6">Our Mission</h3>
              <p className="text-lg text-gray-600 mb-8 leading-relaxed">
                SkillExtractor bridges the gap between job market demands and educational curricula through 
                advanced AI-powered skill analysis. We specialize in cybersecurity domain expertise, helping 
                organizations make data-driven decisions in hiring and curriculum development.
              </p>
              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-gray-700">15+ years of industry experience</span>
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-gray-700">98% accuracy in skill extraction</span>
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-gray-700">500+ satisfied clients worldwide</span>
                </div>
              </div>
            </div>

            <div className="grid gap-6">
              {aboutFeatures.map((feature, index) => {
                const IconComponent = feature.icon;
                return (
                  <div key={index} className="flex gap-4 p-6 bg-gray-50 rounded-xl hover:bg-gray-100 transition-colors">
                    <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg flex items-center justify-center flex-shrink-0">
                      <IconComponent className="h-6 w-6 text-white" />
                    </div>
                    <div>
                      <h4 className="font-bold text-gray-900 mb-2">{feature.title}</h4>
                      <p className="text-gray-600 text-sm leading-relaxed">{feature.description}</p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Cybersecurity Domains */}
          <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl p-8">
            <h3 className="text-2xl font-bold text-gray-900 mb-6 text-center">6 Cybersecurity Domains</h3>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              {[
                'Security Architect',
                'Security Analyst', 
                'Security Consultant',
                'Security Operations',
                'Information Security',
                'Security Testers'
              ].map((domain, index) => (
                <div key={index} className="bg-white rounded-lg p-4 text-center shadow-sm hover:shadow-md transition-shadow">
                  <span className="font-medium text-gray-800">{domain}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section id="services" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">Our Services</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Comprehensive cybersecurity skill analysis solutions across 6 specialized domains
            </p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            {services.map((service) => {
              const IconComponent = service.icon;
              return (
                <div 
                  key={service.id} 
                  className="group bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100 overflow-hidden transform hover:scale-105 cursor-pointer"
                  onClick={() => setCurrentPage('services')}
                >
                  <div className="p-8">
                    <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-300">
                      <IconComponent className="h-8 w-8 text-white" />
                    </div>
                    <h3 className="text-2xl font-bold mb-4 text-gray-900 group-hover:text-green-600 transition-colors">
                      {service.title}
                    </h3>
                    <p className="text-gray-600 mb-6 leading-relaxed">{service.description}</p>

                    <ul className="space-y-2 mb-6">
                      {service.features.map((feature, index) => (
                        <li key={index} className="flex items-center text-sm text-gray-600">
                          <div className="w-1.5 h-1.5 bg-green-500 rounded-full mr-3"></div>
                          {feature}
                        </li>
                      ))}
                    </ul>

                    <div className="flex items-center text-green-600 font-medium group-hover:gap-3 transition-all">
                      Try {service.title}
                      <ArrowRight className="h-4 w-4 ml-2 group-hover:translate-x-1 transition-transform" />
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Features Section - Why Choose Us */}
      <section id="features" className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid lg:grid-cols-2 gap-16 items-center">
            <div>
              <h2 className="text-4xl font-bold text-gray-900 mb-6">Why Choose Our Platform</h2>
              <p className="text-lg text-gray-600 mb-8">
                Designed for both students and educators, our platform delivers accurate insights into
                real-world cybersecurity job trends and helps map course content to industry-required skills.
              </p>

              <div className="space-y-6">
                {features.map((feature, index) => {
                  const IconComponent = feature.icon;
                  return (
                    <div key={index} className="flex gap-4 p-4 bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow border border-gray-100">
                      <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-lg flex items-center justify-center flex-shrink-0">
                        <IconComponent className="h-6 w-6 text-white" />
                      </div>
                      <div>
                        <h4 className="font-bold text-gray-900 mb-2">{feature.title}</h4>
                        <p className="text-gray-600">{feature.description}</p>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Stats Grid */}
            <div className="relative">
              <div className="bg-gradient-to-br from-green-50 to-emerald-50 rounded-3xl p-8">
                <div className="grid grid-cols-2 gap-6">
                  {[
                    { value: '84%+', label: 'Accuracy Rate', color: 'green' },
                    { value: '800+', label: 'Skills Detected', color: 'blue' },
                    { value: '6', label: 'Domains', color: 'purple' },
                    { value: '24/7', label: 'Support', color: 'orange' }
                  ].map((stat, index) => (
                    <div key={index} className="bg-white rounded-2xl p-6 text-center shadow-sm hover:shadow-md transition-shadow">
                      <div className={`text-3xl font-bold mb-2 ${
                        stat.color === 'green' ? 'text-green-600' :
                        stat.color === 'blue' ? 'text-blue-600' :
                        stat.color === 'purple' ? 'text-purple-600' :
                        'text-orange-600'
                      }`}>
                        {stat.value}
                      </div>
                      <div className="text-sm text-gray-600 font-medium">{stat.label}</div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
