// src/App.js
import React, { useState, useEffect } from 'react';

// 正确的导入顺序（BootstrapMade 原样式 → 自定义样式 → 覆盖式样式）
import './styles/style.css';     // BootstrapMade 模板样式
import './styles/index.css';     // 功能组件局部样式（如有）
import './styles/main.css';      // ✅ SASS 编译生成的总样式文件

// 功能组件
import SingleAnalysis from './components/SingleAnalysis';
import BatchProcessing from './components/BatchProcessing';
import CourseMapping from './components/CourseMapping';
import NavBar from './components/NavBar';
import ServicesSection from './components/ServicesSection';

// 若使用 AOS 动画（可选）
import AOS from 'aos';
import 'aos/dist/aos.css';

function App() {
  const [page, setPage] = useState('home');

  // 初始化 AOS 动画
  useEffect(() => {
    AOS.init({ duration: 1000, once: true });
  }, []);

  const renderPage = () => {
    switch (page) {
      case 'home':
        return (
          <section id="hero" className="d-flex align-items-center">
            <div className="container text-center">
              <h1>Welcome to <span>SkillLens</span></h1>
              <h2>AI-powered system for job skill extraction and course mapping</h2>
              <a href="#services" onClick={() => setPage('services')} className="btn-get-started scrollto">
                Get Started
              </a>
            </div>
          </section>
        );

      case 'about':
        return (
          <section id="about" className="about section-bg">
            <div className="container" data-aos="fade-up">
              <div className="section-title">
                <h2>About Us</h2>
                <p>
                  We provide an AI-powered tool to bridge curriculum content with industry skill demands,
                  assisting students and educators with insights derived from real-world job postings.
                </p>
              </div>
              <div className="row content">
                <div className="col-lg-6">
                  <p>
                    Our system extracts key skills from job ads, classifies roles, and maps them to relevant courses.
                    It’s designed for students, instructors, and education planners.
                  </p>
                  <ul>
                    <li><i className="ri-check-double-line"></i> Skill extraction using NLP and LLMs</li>
                    <li><i className="ri-check-double-line"></i> Role classification aligned with cybersecurity domains</li>
                    <li><i className="ri-check-double-line"></i> Curriculum mapping and gap analysis</li>
                  </ul>
                </div>
                <div className="col-lg-6 pt-4 pt-lg-0">
                  <p>
                    Built with modern web technologies and AI models, our system ensures accuracy,
                    scalability, and interpretability. It's currently focused on cybersecurity and
                    expanding to broader domains.
                  </p>
                </div>
              </div>
            </div>
          </section>
        );

      case 'services':
        return <ServicesSection onSelectFeature={setPage} />;

      case 'single':
        return <SingleAnalysis />;

      case 'batch':
        return <BatchProcessing />;

      case 'course':
        return <CourseMapping />;

      default:
        return null;
    }
  };

  return (
    <>
      <NavBar onNavigate={setPage} />
      <main>{renderPage()}</main>
    </>
  );
}

export default App;
