import React from 'react';

const TabNavigation = ({ activeTab, setActiveTab }) => {
  const tabs = [
    { key: 'single', label: 'Single Analysis', icon: 'üîç' },
    { key: 'batch', label: 'Batch Processing', icon: 'üìä' },
    { key: 'mapping', label: 'Course Mapping', icon: 'üéì' }
  ];

  return (
    <div className="card" style={{ padding: '0.25rem', marginBottom: '1.5rem' }}>
      <div className="flex" style={{ gap: '0.25rem' }}>
        {tabs.map((tab) => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            className="flex items-center justify-center px-4 py-3 rounded-lg font-medium"
            style={{
              flex: 1,
              border: 'none',
              cursor: 'pointer',
              transition: 'all 0.2s ease-in-out',
              backgroundColor: activeTab === tab.key ? '#16a34a' : 'transparent',
              color: activeTab === tab.key ? 'white' : '#4b5563',
              boxShadow: activeTab === tab.key ? '0 4px 6px -1px rgba(0, 0, 0, 0.1)' : 'none'
            }}
            onMouseEnter={(e) => {
              if (activeTab !== tab.key) {
                e.target.style.backgroundColor = '#f9fafb';
                e.target.style.color = '#111827';
              }
            }}
            onMouseLeave={(e) => {
              if (activeTab !== tab.key) {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#4b5563';
              }
            }}
          >
            <span className="mr-2">{tab.icon}</span>
            {tab.label}
          </button>
        ))}
      </div>
    </div>
  );
};

export default TabNavigation;
