// src/components/ServicesSection.js
import React from 'react';

const services = [
  {
    id: 'single',
    icon: 'bi-search',
    title: 'Single Analysis',
    description: 'Extract skill keywords from a single job description.',
  },
  {
    id: 'batch',
    icon: 'bi-folder',
    title: 'Batch Processing',
    description: 'Upload multiple job ads and extract structured skill data.',
  },
  {
    id: 'course',
    icon: 'bi-diagram-3',
    title: 'Course Mapping',
    description: 'Align course content with real-world job market requirements.',
  },
];

const ServicesSection = ({ onSelectFeature }) => {
  return (
    <section id="services" className="services section-bg">
      <div className="container">
        <div className="section-title">
          <h2>Services</h2>
          <p>Choose a feature to begin analyzing job-market and curriculum alignment.</p>
        </div>
        <div className="row">
          {services.map((s) => (
            <div className="col-lg-4 d-flex align-items-stretch" key={s.id}>
              <div className="icon-box">
                <div className="icon"><i className={`bi ${s.icon}`}></i></div>
                <h4>{s.title}</h4>
                <p>{s.description}</p>
                <a className="read-more" onClick={() => onSelectFeature(s.id)}>Learn More â†’</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
