// src/App.js
import React, { useState } from 'react';
import './styles/main.css'; // 从 main.scss 编译而来
// 原有功能组件
import SingleAnalysis from './components/SingleAnalysis';
import BatchProcessing from './components/BatchProcessing';
import CourseMapping from './components/CourseMapping';

// 新增导航栏 + 服务卡片
import NavBar from './components/NavBar';
import ServicesSection from './components/ServicesSection';
import DemoHeader from './components/DemoHeader';
function App() {
  const [page, setPage] = useState('home');

  const renderPage = () => {
    switch (page) {
      case 'home':
        return (
          <section id="hero" className="d-flex align-items-center">
            <div className="container text-center">
              <h1>Welcome to <span>SkillLens</span></h1>
              <h2>AI-powered system for job skill extraction and course mapping</h2>
              <div className="btn-group mt-4">
                <button className="btn-get-started" onClick={() => setPage('about')}>About Us</button>
                <button className="btn-learn-more" onClick={() => setPage('services')}>Explore Services</button>
              </div>
            </div>
          </section>
        );
      case 'about':
        return (
          <section id="about" className="about section-bg">
            <div className="container">
              <div className="section-title">
                <h2>About Us</h2>
                <p>We provide an AI-powered tool to bridge curriculum content with industry skill demands, assisting students and educators.</p>
              </div>
            </div>
          </section>
        );
      case 'services':
        return <ServicesSection onSelectFeature={setPage} />;
      default:
        return null;
    }
  };

  return (
    <>
      <NavBar onNavigate={setPage} />
      <main>{renderPage()}</main>
    </>
  );
}

export default App;
