// src/App.js
import React, { useState, useEffect } from 'react';
import './styles/main.css';         // 模板主样式
import './styles/style.css';        // 原有样式（如BootstrapMade样式）
import './styles/index.css';        // 项目自定义样式

import SingleAnalysis from './components/SingleAnalysis';
import BatchProcessing from './components/BatchProcessing';
import CourseMapping from './components/CourseMapping';
import NavBar from './components/NavBar';
import ServicesSection from './components/ServicesSection';

function App() {
  const [page, setPage] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const header = document.querySelector('#header');
      if (header) {
        header.classList.toggle('scrolled', scrollTop > 100);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const renderPage = () => {
    switch (page) {
      case 'home':
        return (
          <section id="hero" className="d-flex align-items-center">
            <div className="container text-center">
              <h1>Welcome to <span>SkillLens</span></h1>
              <h2>AI-powered system for job skill extraction and course mapping</h2>
              <a href="#services" onClick={() => setPage('services')} className="btn-get-started scrollto">Get Started</a>
            </div>
          </section>
        );
      case 'services':
        return <ServicesSection onSelectFeature={setPage} />;
      case 'single':
        return <SingleAnalysis />;
      case 'batch':
        return <BatchProcessing />;
      case 'course':
        return <CourseMapping />;
      default:
        return null;
    }
  };

  return (
    <>
      {/* 模板 header */}
      <header id="header" className="header d-flex align-items-center fixed-top">
        <div className="container d-flex justify-content-between align-items-center">
          <a href="/" className="logo d-flex align-items-center me-auto me-xl-0">
            <h1 className="sitename">SkillLens</h1>
          </a>
          <nav id="navmenu" className="navmenu">
            <ul>
              <li><a href="#hero" onClick={() => setPage('home')}>Home</a></li>
              <li><a href="#about" onClick={() => setPage('about')}>About</a></li>
              <li><a href="#services" onClick={() => setPage('services')}>Services</a></li>
            </ul>
            <i className="mobile-nav-toggle d-xl-none bi bi-list"></i>
          </nav>
          <a className="btn-getstarted" href="#services" onClick={() => setPage('services')}>Get Started</a>
        </div>
      </header>

      {/* 主体内容 */}
      <main className="main" style={{ marginTop: '80px' }}>
        {renderPage()}
      </main>

      {/* 可选 footer */}
      <footer className="footer text-center mt-5 py-4">
        <div className="container">
          <p>© 2025 SkillLens. All Rights Reserved.</p>
        </div>
      </footer>
    </>
  );
}

export default App;
